
public class ACDC {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int A, C, D, ACDC;
		A = 10;
		C = 12;
		D = 13;
		ACDC = (int) (Math.pow(16 , 0) * C + Math.pow(16 , 1) * D + Math.pow(16 , 2) * C + Math.pow(16 , 3) * A ) ;
		System.out.println("Hexadecimal name of the rock band - ACDC encoded is " + ACDC);
	}

}
